  #{isdel,jdbcType=VARCHAR}, #{note,jdbcType=LONGVARCHAR}, #{note2,jdbcType=LONGVARCHAR}, #{gid,jdbcType=INTEGER}
  , #{btype,jdbcType=VARCHAR}
  )
